import { NextApiRequest, NextApiResponse } from 'next'
import { sql } from '@vercel/postgres'

export default async function handler(
  request: NextApiRequest,
  response: NextApiResponse
) {
  try {
    const users = await sql`SELECT * FROM users`
    const userCount = users.rowCount
    const moreThanOneUser = userCount > 1
    // Your data URI
    const dataURI =
      'data:image/png;base64,'

    // Extract base64-encoded data from the data URI
    const base64Data = dataURI.split(',')[1]

    // Decode the base64-encoded data
    const decodedData = atob(base64Data)

    // Calculate the size in bytes
    const sizeInBytes = decodedData.length

    // Convert to kilobytes (KB)
    const sizeInKB = sizeInBytes / 1024

    console.log(`Size: ${sizeInKB} KB`)

    return response.status(200).json({ sizeInKB })
  } catch (error) {
    return response.status(500).json({ error })
  }
}
